import circles from './assets/Circles.svg';
import logo from './assets/logo.svg';
import Typical from 'react-typical';
import React from 'react';
import './App.css';


class SignInError extends React.Component {
  constructor(props){
    super(props);
  }

  componentDidMount() {
    const Circles = new Image();
    Circles.src = circles; // by setting an src, you trigger browser download

    Circles.onload = () => {
      // when it finishes loading, update the component state
      this.setState({ imageIsReady: true });
    }

    const Logo = new Image();
    Logo.src = logo; // by setting an src, you trigger browser download

    Logo.onload = () => {
      // when it finishes loading, update the component state
      this.setState({ imageIsReady: true });
    }
  }
}

function App() {
  const { imageIsReady } = this.state;

  if (!imageIsReady) {
    return <div>Loading image...</div>; // or just return null if you want nothing to be rendered.
  } else {
  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo "/>
        <img src={Circles} className="App-circles" alt="circles"/> 
        <Typical
        className="App-circles-text"
        steps={['Young', 2000, 'Belgian', 2000, 'Freelancer', 2000, 'UX designer', 2000, 'Self-learner', 2000]}
        loop={Infinity}
        wrapper="h1"
        />
      </header>
    </div>
  );
}
}

export default SignInError;
