import React from 'react';
import ReactScrollWheelHandler from "react-scroll-wheel-handler";
import './timeline.scss';
import Timeline from './assets/Timeline.svg';



class timeline extends React.Component { 
  onWheel = (e) => {
    e.preventDefault()
    var container = document.getElementsByClassName('tl')
    var containerScrollPosition = document.getElementsByClassName('tl-svg').scrollLeft
    container.scrollTo({
        top: 0,
        left: largeContainerScrollPosition + e.deltaY,
        behaviour: 'smooth' //if you want smooth scrolling
    })
  }
  render(){
      return (
          <div className="tl">
            <ReactScrollWheelHandler
              upHandler={this.prevIndex}
              downHandler={this.nextIndex}
            >
              <img src={Timeline} className="tl-svg" alt="Timeline"/> 
            </ReactScrollWheelHandler>
          </div>
      );
  }
}

export default timeline;
